import React, { useState,useEffect } from 'react';
import { useSelector, useDispatch } from "react-redux";
import { getData } from './redux/actions/fetchData';

function App() {

  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(getData());
  }, [dispatch]);

  const {data} = useSelector((state) => state.posts);
  const {requesting} = useSelector((state) => state.posts);

  let dataSort = {};



  if (!requesting) {
    const dataSort = data.sort((val1, val2) => val2.cases-val1.cases)
    return (
      dataSort = requesting ? <caption> Loading... </caption> : <> {
        data.map((val, index) => (
          <tr>
            <th scope="row">{index}</th>
            <td>
              <img src={val.countryInfo.flag} width="30"/>  
              <span>{val.country}</span>
            </td>
            <td>{val.cases}</td>
            <td>{val.deaths}</td>
            <td>{val.recovered}</td>
          </tr>
        ))
      } </>
    )
  } else {
    console.log('not ok');
  }

  return (
    <div className="table-responsive">
      <table className="table table-dark">
        <thead className="thead-dark">
          <tr>
            <th scope="col">STT</th>
            <th scope="col">Quốc Gia</th>
            <th scope="col">Số ca măc</th>
            <th scope="col">Tử vong</th>
            <th scope="col">Bình phục</th>
          </tr>
        </thead>
        <tbody>
          {
            dataSort
          }
        </tbody>
      </table>
    </div>
  )
}

export default App
