import React, { useState,useEffect } from 'react';
import { useSelector, useDispatch } from "react-redux";
import { getData } from './redux/actions/fetchData';

function App() {

  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(getData());
  }, [dispatch]);

  const {data} = useSelector((state) => state.posts);
  const {requesting} = useSelector((state) => state.posts);

  let dataSort = {};

  if (data != null) {
    dataSort = data.sort((val1, val2) => val2.cases-val1.cases);
    let indexVN = dataSort.findIndex
    let delVN = dataSort.splice(i => i.country === "Vietnam");
    dataSort.unshift(delVN[0]);
  } else {
    console.log('Loading');
  }


  return (
    <div className="table-responsive">
      <table className="table table-dark">
        <thead className="thead-dark">
          <tr>
            <th scope="col">STT</th>
            <th scope="col">Quốc Gia</th>
            <th scope="col">Số ca măc</th>
            <th scope="col">Tử vong</th>
            <th scope="col">Bình phục</th>
          </tr>
        </thead>
        <tbody>
          {
            Object.keys(dataSort).length === 0 ? <tr><td>"Loandingg"</td></tr> : dataSort.map((val, index) => (
              <tr key={index}>
                <th scope="row">{index}</th>
                <td>
                  <img src={val.countryInfo.flag} width="30"/>  
                  <span>{val.country}</span>
                </td>
                <td>{val.cases}</td>
                <td>{val.deaths}</td>
                <td>{val.recovered}</td>
              </tr>
            ))
          }
        </tbody>
      </table>
    </div>
  )
}

export default App
