import React, { useState,useEffect } from 'react';

function App() {
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(getData());
  }, [dispatch]);

  useEffect(() => {
    setInterval(function(){
      dispatch(getData());
    }, 1000);
  }, []);

  const {data} = useSelector((state) => state.posts);
  let totalCases = 0,
      totalDeaths = 0,
      totalRecovered = 0;


  let dataSort = {};
  if (data != null) {
    // sort data
    dataSort = data.sort((val1, val2) => val2.cases-val1.cases);
    // get index of VN
    let indexVN = dataSort.findIndex(data => data.country === 'Vietnam');
    // get element VN
    let reindexVN = dataSort.splice(indexVN, 1);
    // reindex of VN
    dataSort.unshift(reindexVN[0]);
    // arr has 10 element
    dataSort.length = 10;
    // total
    data.map(val => {
      totalCases+=val.cases;
      totalDeaths+=val.deaths;
      totalRecovered+=val.recovered;
    })
  } else {
    console.log('Loading');
  }


  // search
  const [country, setCountry] = useState('');
  // on submit
  let result = {}
  const getCountryInfor = (e) => {
    e.preventDefault();
    result = data.filter(val => val.country === country);
    if (Object.keys(result).length > 0) {
      let dataShow = `Quốc gia: ${result[0].country}
            <br>Số ca mắc: ${result[0].cases}
            <br>Số ca tử vong: ${result[0].deaths}
            <br>Số ca khỏi bệnh: ${result[0].recovered}
            <br/>Số ca mắc trong ngày: ${result[0].todayCases}
            <br/>Số tử vong trong ngày: ${result[0].todayDeaths}`
      return document.getElementById('showRéult').innerHTML = dataShow;
    }
    // return document.getElementById('showRéult').innerHTML = `Không có kết quả cho ${country}`
  }


  return (
    <div className="table-responsive">
      <form onSubmit={getCountryInfor}>
        <div className="form-row">
          <input type="text" className="form-control col-8 bg-dark text-white" 
                value={country} 
                onChange={e => setCountry(e.target.value)} 
          />
          <button type="submit" className="btn-dark col-4 btn-md location-button" >SEARCH</button>
        </div>
      </form>
      <br/>
      <div id="showRéult"></div>
      <br/>
      <table className="table table-dark">
        <thead className="thead-dark">
          <tr>
            <th scope="col">Tổng số ca nhiễm</th>
            <th scope="col">Tổng số tử vong</th>
            <th scope="col">Tổng số ca khỏi</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>{totalCases}</td>
            <td>{totalDeaths}</td>
            <td>{totalRecovered}</td>
          </tr>
        </tbody>
      </table>
      <br/>

    </div>
  )
}

export default App
