export function fetchData() {
    return function (dispatch) {
        fetch('https://disease.sh/v3/covid-19/countries')
        .then( res => {
            return res.json();
        })
        .then(JSONres => {
            dispatch({
                type: "FETCH_DATA",
                payload: JSONres
            })
        })
        .catch (err => {
            console.log(err);
        })
    }
}
